package org.zhwen.listviewtest;import android.content.Context;import android.support.v4.view.ViewPager;import android.util.AttributeSet;import android.view.MotionEvent;public class MyViewPager extends ViewPager {	private float lastActionDownX, lastActionDownY;	private int xOffset = 0, yOffset = 0;	boolean isLock = false;	boolean isHorizantal = false;	public MyViewPager(Context context) {		super(context);	}	public MyViewPager(Context context, AttributeSet attrs) {		super(context, attrs);	}	@Override	public boolean onInterceptTouchEvent(MotionEvent ev) {		switch (ev.getAction()) {		case MotionEvent.ACTION_DOWN:			lastActionDownX = ev.getX();			lastActionDownY = ev.getY();			xOffset = 0;			yOffset = 0;			isLock = false;			isHorizantal = false;			break;		case MotionEvent.ACTION_MOVE:			if(isLock){				if(isHorizantal)return true;				return super.onInterceptTouchEvent(ev);			}			xOffset = (int) (ev.getX() - lastActionDownX);			yOffset = (int) (ev.getY() - lastActionDownY);			if (Math.abs(xOffset) > 15) {				isLock = true;				isHorizantal = true;				break;			}			if (Math.abs(yOffset) > 15) {				isLock = true;				break;			}			break;		default:			break;		}		return super.onInterceptTouchEvent(ev);	}}